import React, { useRef, useEffect, useState } from "react"; 
import { io } from "socket.io-client";

const socket = io("http://localhost:8000", {
    transports: ["websocket"], // Use WebSocket transport
    reconnection: true, // Enable auto-reconnection
    reconnectionAttempts: 5, // Try to reconnect 5 times before giving up
    reconnectionDelay: 1000, // Delay between reconnection attempts (ms)
});

const App = () => {
    const localVideoRef = useRef();
    const remoteVideoRef = useRef();
    const peerConnectionRef = useRef(null);

    const [isInitiator, setIsInitiator] = useState(false);
    const [remoteSocketId, setRemoteSocketId] = useState("");

    useEffect(() => {
        socket.on("offer", async (data) => {
            if (!isInitiator) {
                setRemoteSocketId(data.from);
                await peerConnectionRef.current.setRemoteDescription(data.offer);
                const answer = await peerConnectionRef.current.createAnswer();
                await peerConnectionRef.current.setLocalDescription(answer);
                socket.emit("answer", {
                    to: data.from,
                    answer,
                });
            }
        });

        socket.on("answer", async (data) => {
            await peerConnectionRef.current.setRemoteDescription(data.answer);
        });

        socket.on("ice-candidate", async (data) => {
            try {
                await peerConnectionRef.current.addIceCandidate(data.candidate);
            } catch (err) {
                console.error("Error adding received ice candidate", err);
            }
        });

        return () => socket.disconnect();
    }, [isInitiator]);

    const startConnection = async () => {
        const localStream = await navigator.mediaDevices.getUserMedia({
            video: true,
            audio: true,
        });
        localVideoRef.current.srcObject = localStream;

        peerConnectionRef.current = new RTCPeerConnection();
        localStream.getTracks().forEach((track) => {
            peerConnectionRef.current.addTrack(track, localStream);
        });

        peerConnectionRef.current.ontrack = (event) => {
            remoteVideoRef.current.srcObject = event.streams[0];
        };

        peerConnectionRef.current.onicecandidate = (event) => {
            if (event.candidate) {
                socket.emit("ice-candidate", {
                    to: remoteSocketId,
                    candidate: event.candidate,
                });
            }
        };

        setIsInitiator(true);
    };

    const connectToUser = async (socketId) => {
        setRemoteSocketId(socketId);

        const offer = await peerConnectionRef.current.createOffer();
        await peerConnectionRef.current.setLocalDescription(offer);

        socket.emit("offer", {
            to: socketId,
            from: socket.id,
            offer,
        });
    };

    return (
        <div>
            <h1>Video Chat App</h1>
            <video ref={localVideoRef} autoPlay muted style={{ width: "300px" }}></video>
            <video ref={remoteVideoRef} autoPlay style={{ width: "300px" }}></video>

            <button onClick={startConnection}>Start Video Chat</button>
            <button
                onClick={() => connectToUser(prompt("Enter remote socket ID"))}
                disabled={!isInitiator}
            >
                Connect to User
            </button>
        </div>
    );
};

export default App;
