import React, { useRef, useState, useEffect } from "react"; 
import socket from "../socket";

const VideoCall = () => {
    const localVideoRef = useRef();
    const remoteVideoRef = useRef();
    const [peerConnection, setPeerConnection] = useState(null);
    const [remoteSocketId, setRemoteSocketId] = useState(null);

    useEffect(() => {
        const pc = new RTCPeerConnection();


        // Handle local media stream
        navigator.mediaDevices.getUserMedia({ video: true, audio: true })
            .then((stream) => {
                localVideoRef.current.srcObject = stream;

                stream.getTracks().forEach((track) => pc.addTrack(track, stream));
            })
            .catch((err) => console.error('Error accessing media devices:', err));

        // Handle incoming remote stream
        pc.ontrack = (event) => {
            remoteVideoRef.current.srcObject = event.streams[0];
        };

        // ICE candidate handling
        pc.onicecandidate = (event) => {
            if (event.candidate) {
                socket.emit('sendCandidate', {
                    candidate: event.candidate,
                    targetId: remoteSocketId
                });
            }
        };

        setPeerConnection(pc);

        // Handle signaling messages
        socket.on('receiveOffer', handleOffer);
        socket.on('receiveAnswer', handleAnswer);
        socket.on('receiveCandidate', handleCandidate);

        // Cleanup
        return () => {
            pc.close();
            socket.off('receiveOffer');
            socket.off('receiveAnswer');
            socket.off('receiveCandidate');
        };
    }, [remoteSocketId]);

    const startCall = async () => {
        if (peerConnection && remoteSocketId) {
            const offer = await peerConnection.createOffer();
            await peerConnection.setLocalDescription(offer);
            socket.emit('sendOffer', { offer, targetId: remoteSocketId });
        }
    };

    const handleOffer = async (offerData) => {
        if (peerConnection) {
            await peerConnection.setRemoteDescription(new RTCSessionDescription(offerData.offer));

            const answer = await peerConnection.createAnswer();
            await peerConnection.setLocalDescription(answer);
            socket.emit('sendAnswer', { answer, targetId: offerData.from });
            setRemoteSocketId(offerData.from);
        }
    };

    const handleAnswer = (answerData) => {
        if (peerConnection) {
            peerConnection.setRemoteDescription(new RTCSessionDescription(answerData.answer));
        }
    };

    const handleCandidate = (candidateData) => {
        if (peerConnection) {
            peerConnection.addIceCandidate(new RTCIceCandidate(candidateData.candidate));
        }
    };

    return (
        <div style={{ padding: "20px" }}>
            <h2>
                <button onClick={() => window.history.back()}>Back</button>
                Welcome {localStorage.getItem("chatName")}
                <button onClick={() => { localStorage.removeItem("chatName"); window.history.back(); }}>
                    Logout
                </button>
            </h2>
            <div>
                <video ref={localVideoRef} autoPlay muted></video>
                <video ref={remoteVideoRef} autoPlay></video>
            </div>
            <div>
                <button onClick={startCall}>Start Call</button>
            </div>
        </div>
    );
};

export default VideoCall;
